import React, { useRef, useState } from "react";
import { useDispatch } from "react-redux";
import { IT_PROJECT_PLAN, IT_REQUESTS, NO_TEMPLATE, PRIVATE_TO_ME, PUBLIC_TO_IT } from "../../constants/projectConstants";
import { createProjectRequest } from "../../services/projectServices";
import "../../styles/ProjectModal.css";

const ProjectModal = ({ closeModal }) => {
    const [projectName, setProjectName] = useState("");
    const [template, setTemplate] = useState(NO_TEMPLATE);
    const [privacy, setPrivacy] = useState(PUBLIC_TO_IT);
    const projectNameInput = useRef();
    const dispatch = useDispatch();

    const submitProjectHandler = () => {
        const project = createProject();
        window.location.pathname = `/home/${userId}/projects/${project.id}`;

        closeModal();
    }

    const createProject = async () => {
        const newProject = {
            user_Id: getUserId(),
            name: projectName,
            template,
            privacy
        }

        return await createProjectRequest(newProject, dispatch)
            .then(data => data)
            .catch(e => console.log(e));
    }

    const getUserId = () => sessionStorage.getItem("id");

    return(
        <div className="project_modal">
            <div className="modal_window">
                <input value={projectName} 
                       onChange={e => setProjectName(e.target.value)} 
                       className="project_name" 
                       placeholder="Project Name"
                       ref={projectNameInput}
                />
                <div className="holder template_holder">
                    <span>Template:</span>
                    <select className="template" onChange={e => setTemplate(e.target.value)}>
                        <option>{ NO_TEMPLATE }</option>
                        <option>{ IT_PROJECT_PLAN }</option>
                        <option>{ IT_REQUESTS }</option>
                    </select>
                </div>
                <div className="holder privacy_holder">
                    <span>Privacy:</span>
                    <select className="privacy" onChange={e => setPrivacy(e.target.value)}>
                        <option>{ PUBLIC_TO_IT }</option>
                        <option>{ PRIVATE_TO_ME }</option>
                    </select>
                </div>
                <div className="submit_part">
                    <button onClick={() => submitProjectHandler()} disabled={!projectName || !projectName.trim()} className="submit_project">
                        Create Project 
                    </button>
                </div>
            </div>
            <div onClick={ closeModal } className="overlay"></div>
        </div>
    )
}

export default ProjectModal;
