import React, { useEffect, useState } from "react";
import { AddIcon, CourseWorkIcon, HideTriangleIcon, ShowTriangleIcon } from "../Common/Icons";
import "../../styles/Projects.css";
import { NavLink } from "react-router-dom";
import ProjectModal from "./ProjectModal";
import { useDispatch, useSelector } from "react-redux";
import { getProjectsRequest } from "../../services/projectServices";
import { COLLABORATIVE, YOUR } from "../../constants/projectConstants";

const Projects = ({ setTab }) => {
    const userId = sessionStorage.getItem("id");
    const projects = useSelector(store => store.projects);
    const dispatch = useDispatch();
    const [openModal, setOpenModal] = useState(false);

    useEffect(() => getProjectsRequest(userId, dispatch), []);

    const ProjectHandler = ({ type, title }) => {
        const [hideProjects, setHideProjects] = useState(false);

        return(
            <div className="project_handler">
                <div className="title_handler">
                    <span onClick={() => setHideProjects(!hideProjects)} className="title">
                        { hideProjects ? <HideTriangleIcon/> : <ShowTriangleIcon/> }
                        { title }
                    </span>
                </div>
                <div className={hideProjects ? "projects projects-hidden" : "projects"}>
                    { 
                        type === YOUR ? projects.map(project => 
                            <NavLink onClick={() => setTab(project.name)} to={`/home/${userId}/projects/${project.id}`} className="project_item_holder">
                                <ProjectItem className="project_item" title={project.name} project={project}/>
                            </NavLink>
                        )
                        : null
                    }
                    <div onClick={() => setOpenModal(true)} className="create_project">
                        <ProjectItem className="project_create" title="Create Project"/>
                    </div>
                </div>
            </div>
        )
    }

    const ProjectItem = ({ className, project, title }) => {
        return(
            <div className={"project " + className}>
                <div className="img_holder">
                    { project ? <CourseWorkIcon/> : <AddIcon/> }
                </div>
                <div className="project_title">{title}</div>
            </div>
        );
    }

    return(
        <div className="projects_component">
            { openModal ? <ProjectModal closeModal={() => setOpenModal(false)} /> : null }
            <ProjectHandler type={YOUR} title="Your projects"/>
            <ProjectHandler type={COLLABORATIVE} title="Collaborative projects"/>
        </div>
    )
}

export default Projects;
