import React, { useState } from "react";
import { AddIcon, InfoIcon, NotificationIcon, ProjectIcon, TaskIcon } from "../Common/Icons";
import TaskModal from "./TaskModal";
import { CREATE } from "../../constants/modalConstants";
import "../../styles/UserBar.css";
import avatar from "../../img/avatar.jpg";
import { useHistory } from "react-router-dom";

const UserBar = ({ tab }) => {
    const history = useHistory();
    const [showAddList, setShowAddList] = useState(false);
    const [openModal, setOpenModal] = useState(false);

    const taskClickHandler = () => {
        setOpenModal(true);
        setShowAddList(false);
    }

    const AddList = () => {
        return(
            <div className="add_list">
                <div onClick={() => taskClickHandler()} className="task">
                    <TaskIcon/> 
                    Task
                </div>
                <div className="project">
                    <ProjectIcon/> Project
                </div>
            </div>
        )
    }

    const ProjectInfo = () => {
        const pathname = history.location.pathname;
        const string = "/projects/";
        const index = pathname.indexOf(string) + string.length;
        const projectId = pathname.substr(index);

        console.log(projectId);

        if (pathname.includes("/projects/")) {
            return <InfoIcon/>
        }

        return null;
    }

    return(
        <div className="user_bar">
            { openModal ? <TaskModal closeModal={() => setOpenModal(false)} type={CREATE}/> : null }
            <div className="tab_handler">
                Tab <span className="tab">{tab}</span>
            </div>
            <div className="icons">
                <div className="icon_holder">
                    <ProjectInfo/>
                </div>
                <div className="icon_holder">
                    <div tabIndex={-1} onFocus={() => setShowAddList(true)} onBlur={() => setShowAddList(false)} className="add_handler">
                        <AddIcon/>
                        { showAddList ? <AddList/> : null}
                    </div>
                </div>
                <div className="icon_holder">
                    <NotificationIcon/>
                </div>
                <div className="avatar_handler">
                    <img className="avatar" src={avatar}></img>
                </div>
            </div>
        </div>
    )
}

export default UserBar;
