import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { getProjectsRequest } from "../../services/projectServices";
import { getTasksRequest } from "../../services/taskServices";
import { TO_DO, IN_PROGRESS, COMPLETED } from "../../constants/taskConstants";
import TaskModal from "../User/TaskModal";
import { CREATE } from "../../constants/modalConstants";
import "../../styles/ProjectBoard.css";

const ProjectBoard = ({ setTab }) => {
    const [openModal, setOpenModal] = useState(false);
    const [category, setCategory] = useState(TO_DO);
    const { id, projectId } = useParams();
    const dispatch = useDispatch();
    const tasks = useSelector(store => store.tasks);
    const projects = useSelector(store => store.projects);
    const project = projects.find(proj => proj.id === Number(projectId));
    const currentTasks = tasks.filter(task => task.project_Id === Number(projectId));

    useEffect(async () => await getTasksRequest(id, dispatch), []);
    useEffect(async () => await getProjectsRequest(id, dispatch), []);

    useEffect(() => {
        if (project) {
            setTab(project.name);
        }
    }, [project]);

    const boardDrop = e => {
        e.preventDefault();
        e.stopPropagation();
        e.target.style.background = "unset";

        // const taskId = e.dataTransfer.getData("task_Id");
        // const task = document.querySelector(`#${taskId}`);

        // const children = e.target.children;
        // const createTask = children[children.length - 1];
        // const emptyTask = children[children.length - 2];

        // const dashIndex = e.target.id.indexOf("-");
        // const boardId = e.target.id.substr(dashIndex + 1);
        // task.attributes["data-board-id"].value = boardId;

        // task.style.display = "flex";
        // emptyTask.style.display = "none";

        // e.target.appendChild(task);
        // e.target.appendChild(emptyTask);
        // e.target.appendChild(createTask);
    }

    const boardDragEnter = e => {
        const createTask = e.target.querySelector(".create_project_task_item");
        const emptyTask = e.target.querySelector(".empty_project_task_item");

        setTimeout(() => {
            emptyTask.style.display = "flex";
            emptyTask.style.background = "rgba(30, 30, 40, .5)";
            e.target.style.background = "linear-gradient(to bottom, rgba(44, 45, 56, 1), rgba(36, 37, 45, 0.15))";
            e.target.appendChild(emptyTask);
            e.target.appendChild(createTask);
        }, 0);
    }

    const boardDragLeave = e => {
        const emptyTask = e.target.querySelector(".empty_project_task_item");

        if (emptyTask) {
            emptyTask.style.display = "none";
        }

        e.target.style.background = "unset";
    }

    const boardDragOver = e => {
        e.preventDefault();
        const afterElement = getDragAfterElement(e.target, e.clientY);
        const task = document.querySelector(".dragging");

        if (!afterElement) {
            e.target.append(task);
        }

        e.target.insertBefore(task, afterElement);
    }

    const taskDrop = e => {
        const boardId = e.target.attributes["data-board-id"].value;

        if (boardId) {
            const board = document.querySelector(`#board-${boardId}`);
            e.target = board;
            boardDrop(e);
        }

        e.preventDefault();
        e.stopPropagation();
    }

    const taskDragLeave = e => {
        const boardId = e.target.attributes["data-board-id"].value;

        if (boardId) {
            const board = document.querySelector(`#board-${boardId}`);
            e.target = board;
            boardDragLeave(e);
        }

        e.preventDefault();
        e.stopPropagation();
    }

    const taskDragEnter = e => {
        const boardId = e.target.attributes["data-board-id"].value;

        if (boardId) {
            const board = document.querySelector(`#board-${boardId}`);
            e.target = board;
            boardDragEnter(e);
        }

        e.preventDefault();
        e.stopPropagation();
    }

    const taskDragStart = e => {
        const target = e.target;
        target.classList.add("dragging");

        e.dataTransfer.setData("task_Id", target.id);

        setTimeout(() => {
            target.style.display = "none";
        }, 0);
    }

    const taskDragEnd = e => {
        e.target.classList.remove("dragging");
        e.target.style.display = "flex";
        e.target.style.boxShadow = "none";
    }

    const taskDragOver = e => {
        e.preventDefault();
        e.stopPropagation();

        if (e.target.className == "project_task_item") {
            e.target.style.boxShadow = "0px 10px 5px 1px rgba(0, 0, 15, .05)";
        }
    }

    const getDragAfterElement = (board, y) => {
        const tasks = [...board.querySelectorAll(".project_task_item:not(.dragging)")];
        const result = tasks.reduce((closest, task) => {
            const box = task.getBoundingClientRect();
            const offset = y - box.top - box.height / 2;

            if (offset < 0 && offset > closest.offset) {
                return { offset: offset, element: task }
            }

            return closest;
        }, { offset: Number.NEGATIVE_INFINITY })

        return result.element;
    }

    const EmptyProjectTaskItem = ({ boardId }) => {
        return(
            <div className="empty_project_task_item"
                 onDragEnter={taskDragEnter}
                 onDragLeave={taskDragLeave}
                 onDrop={taskDrop}
                 data-board-id={boardId}
            >
            </div>
        )
    }

    const ProjectTaskItem = ({ boardId, task }) => {
        return(
            <div className="project_task_item"
                 draggable={true}
                 onDragEnter={taskDragEnter}
                 onDragEnd={taskDragEnd}
                 onDragLeave={taskDragLeave}
                 onDragStart={taskDragStart}
                 onDragOver={taskDragOver}
                 onDrop={taskDrop}
                 id={"task-" + task.id}
                 data-board-id={boardId}
            >
                <p onDragEnter={taskDragEnter}
                   onDragLeave={taskDragLeave}
                   onDrop={taskDrop}
                   data-board-id={boardId}
                >{ task.name }</p>
            </div>
        )
    }

    const CreateProjectTaskItem = ({ boardId, category }) => {
        const openModalHandler = () => {
            setOpenModal(true);
            setCategory(category);
        }

        return(
            <div className="create_project_task_item"
                 onDragEnter={taskDragEnter}
                 onDragLeave={taskDragLeave}
                 onDrop={taskDrop}
                 data-board-id={boardId}
            >
                <span onClick={openModalHandler}
                      onDragEnter={taskDragEnter}
                      onDragLeave={taskDragLeave}
                      onDrop={taskDrop}
                      data-board-id={boardId}
                >Add task</span>
            </div>
        )
    }

    const renderTaskModal = () => {
        return(
            openModal ? <TaskModal closeModal={() => setOpenModal(false)} 
                                   type={CREATE} 
                                   category={category.category}
                                   currentProject={project} 
                        /> : null 
        )
    }

    const renderBoards = () => {
        const categories = [TO_DO, IN_PROGRESS, COMPLETED];

        return (
            categories.map((ctgry, index) => 
                <div className="board">
                    <div className="project_title">
                        <span>{ ctgry }</span>
                    </div>
                    <div className="project_tasks"
                         id={"board-" + index}
                         onDrop={boardDrop}
                         onDragOver={boardDragOver}
                         onDragEnter={boardDragEnter}
                         onDragLeave={boardDragLeave}
                    >
                        { currentTasks.map(task => task.category === ctgry ? <ProjectTaskItem boardId={index} task={task} /> : null) }
                        <EmptyProjectTaskItem boardId={index}/>
                        <CreateProjectTaskItem boardId={index} category={ ctgry }/>
                    </div>
                </div>
            )
        )
    }

    return(
        <div className="project_board">
            { renderTaskModal() }
            { renderBoards() }
        </div>
    )
}

export default ProjectBoard;
