import React, { useState } from "react";
import { TEXTBOX, TEXTAREA } from "../../constants/projectPointConstants";
import "../../styles/ProjectPointsModal.css";

const ProjectPointsModal = ({ values, closeModal }) => {
    const renderTextBox = (projectPoint) => {
        const [value, setValue] = useState(projectPoint.value);

        return(
            <div className="textbox">
                <input placeholder={ projectPoint.title }
                       value={value}
                />
            </div>
        )
    }

    const renderTextArea = (value) => {
        return(
            <div className="textarea">
                <textarea placeholder={ value.title }
                />
            </div>
        )
    }

    return(
        <div className="project_points_modal">
            <div className="modal_window">
                { 
                    values.map(value => {
                        switch (value.type) {
                            case TEXTBOX:
                                return renderTextBox(value);
                            case TEXTAREA:
                                return renderTextArea(value);
                        }
                    }) 
                }
            </div>
            <div onClick={ closeModal } className="overlay"></div>
        </div>
    )
}

export default ProjectPointsModal;
