import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { getProjectsRequest } from "../../services/projectServices";
import { getTasksRequest } from "../../services/taskServices";
import { TO_DO, IN_PROGRESS, COMPLETED } from "../../constants/taskConstants";
import "../../styles/ProjectBoard.css";

const ProjectBoard = ({ setTab }) => {
    const { id, projectId } = useParams();
    const dispatch = useDispatch();
    const tasks = useSelector(store => store.tasks);
    const projects = useSelector(store => store.projects);
    const project = projects.find(proj => proj.id === Number(projectId));
    const currentTasks = tasks.filter(task => task.project_Id === Number(projectId));

    useEffect(async () => await getTasksRequest(id, dispatch), []);
    useEffect(async () => await getProjectsRequest(id, dispatch), []);

    useEffect(() => {
        if (project) {
            setTab(project.name);
        }
    }, [project]);

    const ProjectTaskItem = ({ name }) => {
        return(
            <div className="project_task_item">
                <p>{ name }</p>
            </div>
        )
    }

    const CreateProjectTaskItem = () => {
        return(
            <div className="create_project_task_item">
                <span>Add task</span>
            </div>
        )
    }

    return(
        <div className="project_board">
            <div className="column col-todo">
                <div className="project_title">
                    <span>{ TO_DO }</span>
                </div>
                <div className="project_tasks">
                    { currentTasks.map(task => task.category === TO_DO ? <ProjectTaskItem name={task.name} /> : null) }
                    <CreateProjectTaskItem/>
                </div>
            </div>
            <div className="column col-doing">
                <div className="project_title">
                    <span>{ IN_PROGRESS }</span>
                </div>
                <div className="project_tasks">
                    { currentTasks.map(task => task.category === IN_PROGRESS ? <ProjectTaskItem name={task.name} /> : null) }
                    <CreateProjectTaskItem/>
                </div>
            </div>
            <div className="column col-done">
                <div className="project_title">
                    <span>{ COMPLETED }</span>
                </div>
                <div className="project_tasks">
                    { currentTasks.map(task => task.category === COMPLETED ? <ProjectTaskItem name={task.name} /> : null) }
                    <CreateProjectTaskItem/>
                </div>
            </div>
        </div>
    )
}

export default ProjectBoard;
