import React, { useRef, useState } from "react";
import { TEXTBOX, TEXTAREA } from "../../constants/projectPointConstants";
import "../../styles/ProjectPointsModal.css";

const ProjectPointsModal = ({ values, closeModal }) => {
    const inputs = [];

    const TextBox = ({ projectPoint }) => {
        const [value, setValue] = useState(projectPoint.value);
        const ref = useRef();

        if (ref.current) {
            inputs.push(ref);
        }

        return(
            <div className="textbox">
                <span className="title">{projectPoint.title}:</span>
                <input value={ value }
                       className="line"
                       onChange={e => setValue(e.target.value)}
                       ref={ref}
                />
            </div>
        )
    }

    const TextArea = ({ projectPoint }) => {
        const [value, setValue] = useState(projectPoint.value);
        const ref = useRef();

        if (ref.current) {
            inputs.push(ref);
        }

        return(
            <div className="textarea">
                <span className="title">{projectPoint.title}:</span>
                <textarea value={ value }
                          className="box"
                          onChange={e => setValue(e.target.value)}
                          ref={ref}
                />
            </div>
        )
    }

    const submitUpdating = () => {
        console.log(inputs);
    }

    return(
        <div className="project_points_modal">
            <div className="modal_window">
                { 
                    values.map(value => {
                        switch (value.type) {
                            case TEXTBOX:
                                return <TextBox projectPoint={value} />;
                            case TEXTAREA:
                                return <TextArea projectPoint={value} />;
                        }
                    }) 
                }
                <div className="submit_part">
                    <button onClick={submitUpdating} className="update_info">
                        Update Info
                    </button>
                </div>
            </div>
            <div onClick={ closeModal } className="overlay"></div>
        </div>
    )
}

export default ProjectPointsModal;
