import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { getProjectsRequest } from "../../services/projectServices";
import { getTasksRequest } from "../../services/taskServices";
import { TO_DO, IN_PROGRESS, COMPLETED } from "../../constants/taskConstants";
import TaskModal from "../User/TaskModal";
import { CREATE } from "../../constants/modalConstants";
import "../../styles/ProjectBoard.css";

const ProjectBoard = ({ setTab }) => {
    const [openModal, setOpenModal] = useState(false);
    const [category, setCategory] = useState(TO_DO);
    const { id, projectId } = useParams();
    const dispatch = useDispatch();
    const tasks = useSelector(store => store.tasks);
    const projects = useSelector(store => store.projects);
    const project = projects.find(proj => proj.id === Number(projectId));
    const currentTasks = tasks.filter(task => task.project_Id === Number(projectId));

    useEffect(async () => await getTasksRequest(id, dispatch), []);
    useEffect(async () => await getProjectsRequest(id, dispatch), []);

    useEffect(() => {
        if (project) {
            setTab(project.name);
        }
    }, [project]);

    const columnDrop = e => {
        e.preventDefault();
        e.target.style.background = "unset";

        const taskId = e.dataTransfer.getData("task_Id");
        const task = document.querySelector(`#${taskId}`);

        task.style.display = "flex";

        e.target.appendChild(task);
    }

    const columnDragEnter = e => {
        e.target.style.background = "linear-gradient(to bottom, rgba(44, 45, 56, 1), rgba(36, 37, 45, 0.15))";
    }

    const columnDragLeave = e => {
        e.target.style.background = "unset";
    }

    const columnDragOver = e => {
        e.preventDefault();
    }

    const taskDragEnter = e => {
        e.preventDefault();
    }

    const taskDragStart = e => {
        const target = e.target;

        e.dataTransfer.setData("task_Id", target.id);

        setTimeout(() => {
            target.style.display = "none";
        }, 0);
    }

    const taskDragOver = e => {
        e.stopPropagation();
    }

    const ProjectTaskItem = ({ task }) => {
        return(
            <div className="project_task_item"
                 draggable={true}
                 onDragStart={taskDragStart}
                 onDragOver={taskDragOver}
                 id={"task-" + task.id}
            >
                <p>{ task.name }</p>
            </div>
        )
    }

    const CreateProjectTaskItem = category => {
        const openModalHandler = () => {
            setOpenModal(true);
            setCategory(category);
        }

        return(
            <div className="create_project_task_item">
                <span onClick={openModalHandler}>Add task</span>
            </div>
        )
    }

    const renderTaskModal = () => {
        return(
            openModal ? <TaskModal closeModal={() => setOpenModal(false)} 
                                   type={CREATE} 
                                   category={category.category}
                                   currentProject={project} 
                        /> : null 
        )
    }

    const renderColumns = () => {
        const categories = [TO_DO, IN_PROGRESS, COMPLETED];

        return (
            categories.map(ctgry => 
                <div className="column">
                    <div className="project_title">
                        <span>{ ctgry }</span>
                    </div>
                    <div className="project_tasks"
                         onDrop={columnDrop}
                         onDragOver={columnDragOver}
                         onDragEnter={columnDragEnter}
                         onDragLeave={columnDragLeave}
                    >
                        { currentTasks.map(task => task.category === ctgry ? <ProjectTaskItem task={task} /> : null) }
                        <CreateProjectTaskItem category={ ctgry }/>
                    </div>
                </div>
            )
        )
    }

    return(
        <div className="project_board">
            { renderTaskModal() }
            { renderColumns() }
        </div>
    )
}

export default ProjectBoard;
