import React, { useEffect, useState } from "react";
import { NavLink, Route, Switch, useParams } from "react-router-dom";
import { HOME, LOGOUT, PROJECTS, PROFILE, MYTASKS } from "../../constants/tabConstants";
import UserBar from "./UserBar";
import Home from "./Home";
import { HomeIcon, LogoutIcon, ProfileIcon, ProjectIcon, TaskIcon } from "../Common/Icons";
import Tasks from "./Tasks";
import Projects from "./Projects";
import ProjectBoard from "./ProjectBoard";
import "../../styles/UserApp.css";
import { useDispatch, useSelector } from "react-redux";
import { getProjectsRequest } from "../../services/projectServices";

const UserApp = () => {

    const getTab = () => {
        let path = window.location.pathname;
        let index = path.indexOf(id);

        path = path.substr(index);

        switch (path) {
            case `${id}`: return HOME;
            case `${id}/tasks`: return MYTASKS;
            case `${id}/projects`: return PROJECTS;
            case `${id}/profile`: return PROFILE;
            default: {
                const idIndex = path.lastIndexOf("/");
                const projectId = Number(path.substr(idIndex + 1));
                const project = projects.find(proj => proj.id === projectId);
                return project.name;
            }
        }
    }
    const { id } = useParams();
    const dispatch = useDispatch();
    const projects = useSelector(store => store.projects);
    const [tab, setTab] = useState(getTab());

    useEffect(async () => await getProjectsRequest(id, dispatch), []);




    const clearStorage = () => {
        const token = "accessToken";
        const userId = "id";

        sessionStorage.removeItem(token)
        sessionStorage.removeItem(userId);
    }

    const CustomLink = ({ tabName, url, icon, logout }) => {
        return(
            <NavLink onClick={() => logout ? clearStorage() : setTab(tabName)} 
                     className={tab === tabName ? "link focused" : "link"} 
                     to={`${url}`}>
                {icon}
                {tabName}
            </NavLink>
        )
    }

    return(
        <div className="user_app">
            <div className="container">
                <div className="navbar">
                    <div className="logo">Logo</div>
                    <div className="tabs">
                        <CustomLink tabName={HOME} url={`/home/${id}`} icon={<HomeIcon/>} logout={false}/>
                        <CustomLink tabName={MYTASKS} url={`/home/${id}/tasks`} icon={<TaskIcon/>} logout={false}/>
                        <CustomLink tabName={PROJECTS} url={`/home/${id}/projects`} icon={<ProjectIcon/>} logout={false}/>
                        <CustomLink tabName={PROFILE} url={`/home/${id}/profile`} icon={<ProfileIcon/>} logout={false}/>
                    </div>
                    <div className="logout">
                        <CustomLink tabName={LOGOUT} url="/" icon={<LogoutIcon/>} logout={true}/>
                    </div>
                </div>
                <div className="content">
                    <UserBar tab={tab}/>
                    <Switch>
                        <Route exact path="/home/:id/tasks" component={Tasks} />
                        <Route exact path="/home/:id/projects/:projectId" component={ProjectBoard} />
                        <Route exact path="/home/:id/projects">
                            <Projects setTab={setTab}/>    
                        </Route>
                        <Route exact path="/home/:id" component={Home} />
                    </Switch>
                </div>
            </div>
        </div>
    )
}

export default UserApp;
